using Microsoft.AspNetCore.Mvc;
using Microsoft.EntityFrameworkCore;
using Pustok.Data;
using Pustok.Models;

namespace Pustok.Areas.Manage.Controllers;

[Area("Manage")]
public class FeatureController : Controller
{
    private readonly AppDbContext _context;

    public FeatureController(AppDbContext context)
    {
        _context = context;
    }

    public async Task<IActionResult> Index()
    {
        return View(await _context.Features.ToListAsync());
    }

    public async Task<IActionResult> Details(int? id)
    {
        if (id == null) return NotFound();

        var feature = await _context.Features
            .FirstOrDefaultAsync(m => m.Id == id);
        if (feature == null) return NotFound();

        return View(feature);
    }

    public IActionResult Create()
    {
        return View();
    }

    [HttpPost]
    [ValidateAntiForgeryToken]
    public async Task<IActionResult> Create([Bind("Id,Title,Icon,Description")] Featured feature)
    {
        if (ModelState.IsValid)
        {
            _context.Add(feature);
            await _context.SaveChangesAsync();
            return RedirectToAction(nameof(Index));
        }
        return View(feature);
    }

    public async Task<IActionResult> Edit(int? id)
    {
        if (id == null) return NotFound();

        var feature = await _context.Features.FindAsync(id);
        if (feature == null) return NotFound();
        return View(feature);
    }

    [HttpPost]
    [ValidateAntiForgeryToken]
    public async Task<IActionResult> Edit(int id, [Bind("Id,Title,Icon,Description")] Featured feature)
    {
        if (id != feature.Id) return NotFound();

        if (ModelState.IsValid)
        {
            try
            {
                _context.Update(feature);
                await _context.SaveChangesAsync();
            }
            catch (DbUpdateConcurrencyException)
            {
                if (!FeatureExists(feature.Id))
                    return NotFound();
                else
                    throw;
            }
            return RedirectToAction(nameof(Index));
        }
        return View(feature);
    }

    public async Task<IActionResult> Delete(int? id)
    {
        if (id == null) return NotFound();

        var feature = await _context.Features
            .FirstOrDefaultAsync(m => m.Id == id);
        if (feature == null) return NotFound();

        return View(feature);
    }

    [HttpPost, ActionName("Delete")]
    [ValidateAntiForgeryToken]
    public async Task<IActionResult> DeleteConfirmed(int id)
    {
        var feature = await _context.Features.FindAsync(id);
        if (feature != null)
        {
            _context.Features.Remove(feature);
        }

        await _context.SaveChangesAsync();
        return RedirectToAction(nameof(Index));
    }

    private bool FeatureExists(int id)
    {
        return _context.Features.Any(e => e.Id == id);
    }
}

using Microsoft.AspNetCore.Mvc;
using Microsoft.EntityFrameworkCore;
using Pustok.Data;
using Pustok.Models;

namespace Pustok.Areas.Manage.Controllers;

[Area("Manage")]
public class TagController(AppDbContext context) : Controller
{
    public async Task<IActionResult> Index()
    {
        var tags = await context.Tags.ToListAsync();
        return View(tags);
    }

    public IActionResult Create()
    {
        return View();
    }

    [HttpPost]
    [ValidateAntiForgeryToken]
    public async Task<IActionResult> Create(Tag tag)
    {
        if (!ModelState.IsValid)
        {
            return View(tag);
        }

        if (context.Tags.Any(t => t.Name == tag.Name))
        {
            ModelState.AddModelError("Name", "This tag name already exists");
            return View(tag);
        }

        context.Tags.Add(tag);
        await context.SaveChangesAsync();
        return RedirectToAction(nameof(Index));
    }

    public async Task<IActionResult> Edit(int id)
    {
        var tag = await context.Tags.FindAsync(id);
        if (tag == null) return NotFound();
        return View(tag);
    }

    [HttpPost]
    [ValidateAntiForgeryToken]
    public async Task<IActionResult> Edit(int id, Tag tag)
    {
        if (id != tag.Id) return NotFound();

        if (!ModelState.IsValid)
        {
            return View(tag);
        }

        if (context.Tags.Any(t => t.Name == tag.Name && t.Id != id))
        {
            ModelState.AddModelError("Name", "This tag name already exists");
            return View(tag);
        }

        try
        {
            context.Update(tag);
            await context.SaveChangesAsync();
        }
        catch (DbUpdateConcurrencyException)
        {
            if (!TagExists(tag.Id))
            {
                return NotFound();
            }
            throw;
        }

        return RedirectToAction(nameof(Index));
    }

    public async Task<IActionResult> Delete(int id)
    {
        var tag = await context.Tags
            .Include(t => t.BookTags)
            .FirstOrDefaultAsync(t => t.Id == id);

        if (tag == null) return NotFound();

        if (tag.BookTags.Any())
        {
            TempData["Error"] = "This tag is being used by books and cannot be deleted.";
            return RedirectToAction(nameof(Index));
        }

        context.Tags.Remove(tag);
        await context.SaveChangesAsync();
        return RedirectToAction(nameof(Index));
    }

    private bool TagExists(int id)
    {
        return context.Tags.Any(e => e.Id == id);
    }
}

using System.ComponentModel.DataAnnotations;

namespace Pustok.Models;

public class Tag:AuditEntity
{
    [Required(ErrorMessage = "Tag name is required.")]
    [StringLength(50, ErrorMessage = "Tag name cannot exceed 50 characters.")]
    [Display(Name = "Tag Name")]
    public string Name { get; set; } = string.Empty;

    public List<BookTag> BookTags { get; set; } = new();
}

using System.ComponentModel.DataAnnotations;

namespace Pustok.Models;

public class Tag:BaseEntity
{
    [Required(ErrorMessage = "Tag name is required.")]
    [StringLength(50, ErrorMessage = "Tag name cannot exceed 50 characters.")]
    [Display(Name = "Tag Name")]
    public string Name { get; set; } = string.Empty;

    public DateTime CreatedDate { get; set; } = DateTime.Now;

    public List<BookTag> BookTags { get; set; } = new();
}
